            var isLightMode = $(document.body).hasClass(classes.lightMode);

            if (isDarkMode) {
                loadVideo(darkVideo, 'dark', isActiveSlide);
            } else if (isLightMode) {
                loadVideo(lightVideo, 'light', isActiveSlide);
            }
